-- 04_functions.sql (SOURCE project)
SELECT pg_get_functiondef(p.oid) AS sql
FROM pg_proc p
JOIN pg_namespace n ON n.oid = p.pronamespace
WHERE n.nspname IN ('public','auth','storage')
ORDER BY n.nspname, p.proname, p.oid;




