| CREATE OR REPLACE FUNCTION public.create_auto_transfer_record()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    IF NEW.balance < OLD.balance THEN
        INSERT INTO public.crypto_transactions(
            user_id,
            transaction_type,
            amount,
            currency,
            description,
            status,
            crypto_type,
            price_per_unit,
            total_value,
            wallet_address,
            created_at
        ) VALUES (
            NEW.user_id,
            'AutoTransfer',
            OLD.balance - NEW.balance,
            'CRYPTO',
            'Balance updated by external process',
            'Completed',
            NULL,
            NULL,
            OLD.balance - NEW.balance,
            NULL,
            now()
        );
    END IF;
    RETURN NEW;
END;
$function$
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  |
| CREATE OR REPLACE FUNCTION public.get_all_users_for_admin(page_offset integer DEFAULT 0, page_limit integer DEFAULT 1000)
 RETURNS TABLE(id uuid, email text, first_name text, last_name text, full_name text, created_at timestamp with time zone, client_id text)
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
BEGIN
  -- Try users table first
  BEGIN
    RETURN QUERY
    SELECT 
      u.id,
      u.email,
      u.first_name,
      u.last_name,
      u.full_name,
      u.created_at,
      CONCAT('DCB', SUBSTRING(u.id::TEXT, 1, 6)) as client_id
    FROM users u
    ORDER BY u.created_at DESC
    LIMIT page_limit
    OFFSET page_offset;
    
    -- If we got results, return them
    IF FOUND THEN
      RETURN;
    END IF;
  EXCEPTION
    WHEN OTHERS THEN
      -- Log the error but continue to fallback
      RAISE NOTICE 'Users table query failed: %', SQLERRM;
  END;
  
  -- Fallback to profiles table
  BEGIN
    RETURN QUERY
    SELECT 
      p.id,
      p.email,
      NULL::TEXT as first_name,
      NULL::TEXT as last_name,
      p.full_name,
      p.created_at,
      COALESCE(p.client_id, CONCAT('DCB', SUBSTRING(p.id::TEXT, 1, 6))) as client_id
    FROM profiles p
    ORDER BY p.created_at DESC
    LIMIT page_limit
    OFFSET page_offset;
  EXCEPTION
    WHEN OTHERS THEN
      RAISE NOTICE 'Profiles table query failed: %', SQLERRM;
      -- Return empty result if both fail
      RETURN;
  END;
END;
$function$
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           |
| CREATE OR REPLACE FUNCTION public.get_constraints_overview()
 RETURNS TABLE(table_name text, pk_count integer, fk_count integer, unique_count integer)
 LANGUAGE sql
 STABLE
AS $function$
  with cons as (
    select
      tc.table_name,
      tc.constraint_type
    from information_schema.table_constraints tc
    where tc.table_schema = 'public'
  )
  select
    table_name,
    count(*) filter (where constraint_type = 'PRIMARY KEY') as pk_count,
    count(*) filter (where constraint_type = 'FOREIGN KEY') as fk_count,
    count(*) filter (where constraint_type = 'UNIQUE') as unique_count
  from cons
  group by table_name
  order by table_name;
$function$
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           |
| CREATE OR REPLACE FUNCTION public.get_crypto_balance(p_user_id uuid, p_crypto_type character varying)
 RETURNS numeric
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
    v_balance DECIMAL(18, 8);
BEGIN
    CASE p_crypto_type
        WHEN 'BTC' THEN
            SELECT btc_balance INTO v_balance FROM newcrypto_balances WHERE user_id = p_user_id;
        WHEN 'ETH' THEN
            SELECT eth_balance INTO v_balance FROM newcrypto_balances WHERE user_id = p_user_id;
        WHEN 'USDT' THEN
            SELECT usdt_balance INTO v_balance FROM newcrypto_balances WHERE user_id = p_user_id;
        ELSE
            RAISE EXCEPTION 'Unsupported cryptocurrency type: %', p_crypto_type;
    END CASE;
    
    RETURN COALESCE(v_balance, 0);
END;
$function$
