| CREATE OR REPLACE FUNCTION public.get_rls_overview()
 RETURNS TABLE(table_name text, rls_enabled boolean, rls_forced boolean, policy_count integer, policies_summary text)
 LANGUAGE sql
 STABLE
AS $function$
  SELECT
    c.relname                      AS table_name,
    c.relrowsecurity               AS rls_enabled,
    c.relforcerowsecurity          AS rls_forced,
    COALESCE(p.policy_count, 0)    AS policy_count,
    p.policies                     AS policies_summary
  FROM pg_class c
  JOIN pg_namespace n ON n.oid = c.relnamespace
  LEFT JOIN LATERAL (
    SELECT
      COUNT(*) AS policy_count,
      string_agg(
        format(
          '%s | %s | roles: %s | permissive: %s | using: %s | with_check: %s',
          pp.policyname,
          pp.cmd,
          COALESCE(array_to_string(pp.roles, ','), 'PUBLIC'),
          pp.permissive,
          COALESCE(pp.qual, '<none>'),
          COALESCE(pp.with_check, '<none>')
        ),
        E'\n'
      ) AS policies
    FROM pg_policies pp
    WHERE pp.schemaname = 'public' AND pp.tablename  = c.relname
  ) p ON TRUE
  WHERE c.relkind IN ('r','p')  -- ordinary/partitioned tables
    AND n.nspname = 'public'
  ORDER BY c.relname;
$function$
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              |
| CREATE OR REPLACE FUNCTION public.get_table_estimates()
 RETURNS TABLE(table_name text, est_rows bigint)
 LANGUAGE sql
 STABLE
AS $function$
  select
    c.relname as table_name,
    greatest(c.reltuples::bigint, 0) as est_rows
  from pg_class c
  join pg_namespace n on n.oid = c.relnamespace
  where c.relkind in ('r','p')
    and n.nspname = 'public'
  order by c.relname;
$function$
