                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    |
| CREATE OR REPLACE FUNCTION public.get_tax_stats_by_user(p_user_id uuid)
 RETURNS TABLE(pending_count bigint, pending_amount numeric, on_hold_count bigint, on_hold_amount numeric, paid_count bigint, paid_amount numeric)
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
BEGIN
  RETURN QUERY
  SELECT 
    COUNT(*) FILTER (WHERE status = 'pending') as pending_count,
    COALESCE(SUM(tax_amount) FILTER (WHERE status = 'pending'), 0) as pending_amount,
    COUNT(*) FILTER (WHERE status = 'on_hold') as on_hold_count,
    COALESCE(SUM(tax_amount) FILTER (WHERE status = 'on_hold'), 0) as on_hold_amount,
    COUNT(*) FILTER (WHERE status = 'paid') as paid_count,
    COALESCE(SUM(tax_amount) FILTER (WHERE status = 'paid'), 0) as paid_amount
  FROM public.taxes
  WHERE user_id = p_user_id AND is_active = true;
END;
$function$
