| CREATE OR REPLACE FUNCTION public.get_user_count_for_admin()
 RETURNS integer
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
BEGIN
  -- This function runs with the privileges of the function owner (bypasses RLS)
  RETURN (SELECT COUNT(*) FROM users);
EXCEPTION
  WHEN OTHERS THEN
    -- If users table fails, try profiles table
    BEGIN
      RETURN (SELECT COUNT(*) FROM profiles);
    EXCEPTION
      WHEN OTHERS THEN
        RETURN 0;
    END;
END;
