| CREATE OR REPLACE FUNCTION public.process_crypto_transfer(p_user_id uuid, p_crypto_type text, p_amount numeric, p_fee numeric, p_total_amount numeric, p_wallet_address text, p_current_balance numeric)
 RETURNS TABLE(success boolean, new_balance numeric)
 LANGUAGE plpgsql
AS $function$
DECLARE
    updated_balance numeric;
BEGIN
    -- Ensure sufficient balance
    IF p_total_amount > p_current_balance THEN
        RETURN QUERY SELECT false, p_current_balance;
        RETURN;
    END IF;

    -- Deduct from balance
    UPDATE public.crypto_balances
    SET balance = balance - p_total_amount,
        updated_at = now()
    WHERE user_id = p_user_id
    RETURNING balance INTO updated_balance;

    -- Record transaction (Pending or Completed as chosen)
    INSERT INTO public.crypto_transactions(
        user_id,
        transaction_type,
        amount,
        currency,
        description,
        status,
        crypto_type,
        price_per_unit,
        total_value,
        wallet_address,
        created_at
    ) VALUES (
        p_user_id,
        'Transfer',
        p_amount,
        'CRYPTO',
        'Transfer of ' || p_amount || ' ' || p_crypto_type || ' to ' || p_wallet_address,
        'Pending',  -- you can update status to 'Completed' when approved
        p_crypto_type,
        NULL,
        p_total_amount,
        p_wallet_address,
        now()
    );

    RETURN QUERY SELECT true, updated_balance;
END;
$function$
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       |
| CREATE OR REPLACE FUNCTION public.process_real_crypto_transfer(p_user_id uuid, p_crypto_type character varying, p_network character varying, p_amount numeric, p_gas_fee numeric, p_total_amount numeric, p_wallet_address text, p_current_balance numeric)
 RETURNS json
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
    v_balance_column TEXT;
    v_new_balance DECIMAL(18, 8);
    v_transaction_id UUID;
    v_result JSON;
BEGIN
    -- Determine the balance column based on crypto type
    CASE p_crypto_type
        WHEN 'BTC' THEN v_balance_column := 'btc_balance';
        WHEN 'ETH' THEN v_balance_column := 'eth_balance';
        WHEN 'USDT' THEN v_balance_column := 'usdt_balance';
        ELSE RAISE EXCEPTION 'Unsupported cryptocurrency type: %', p_crypto_type;
    END CASE;
    
    -- Check if user has sufficient balance
    IF p_total_amount > p_current_balance THEN
        RAISE EXCEPTION 'Insufficient balance. Required: %, Available: %', p_total_amount, p_current_balance;
    END IF;
    
    -- Calculate new balance
    v_new_balance := p_current_balance - p_total_amount;
    
    -- Create crypto transaction record
    INSERT INTO crypto_transactions (
        user_id,
        crypto_type,
        transaction_type,
        amount,
        currency,
        price_per_unit,
        total_value,
        wallet_address,
        network,
        gas_fee,
        status,
        description
    ) VALUES (
        p_user_id,
        p_crypto_type,
        'Transfer',
        p_amount,
        p_crypto_type,
        1.0,
        p_total_amount,
        p_wallet_address,
        p_network,
        p_gas_fee,
        'Pending',
        'Crypto transfer to ' || SUBSTRING(p_wallet_address, 1, 10) || '...'
    ) RETURNING id INTO v_transaction_id;
    
    -- Update user's crypto balance using dynamic SQL
    EXECUTE format('
        UPDATE newcrypto_balances 
        SET %I = $1, updated_at = NOW() 
        WHERE user_id = $2
    ', v_balance_column) 
    USING v_new_balance, p_user_id;
    
    -- Return success result
    v_result := json_build_object(
        'success', true,
        'transaction_id', v_transaction_id,
        'new_balance', v_new_balance,
        'crypto_type', p_crypto_type
    );
    
    RETURN v_result;
    
EXCEPTION
    WHEN OTHERS THEN
        -- Return error result
        v_result := json_build_object(
            'success', false,
            'error', SQLERRM
        );
        RETURN v_result;
END;
$function$
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           |
| CREATE OR REPLACE FUNCTION public.search_users_for_admin(search_term text, page_limit integer DEFAULT 100)
 RETURNS TABLE(id uuid, email text, first_name text, last_name text, full_name text, created_at timestamp with time zone, client_id text)
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
BEGIN
  -- Try users table first
  BEGIN
    RETURN QUERY
    SELECT 
      u.id,
      u.email,
      u.first_name,
      u.last_name,
      u.full_name,
      u.created_at,
      CONCAT('DCB', SUBSTRING(u.id::TEXT, 1, 6)) as client_id
    FROM users u
    WHERE 
      u.email ILIKE '%' || search_term || '%' OR
      u.first_name ILIKE '%' || search_term || '%' OR
      u.last_name ILIKE '%' || search_term || '%' OR
      u.full_name ILIKE '%' || search_term || '%'
    ORDER BY u.created_at DESC
    LIMIT page_limit;
    
    IF FOUND THEN
      RETURN;
    END IF;
  EXCEPTION
    WHEN OTHERS THEN
      RAISE NOTICE 'Users search failed: %', SQLERRM;
  END;
  
  -- Fallback to profiles table
  BEGIN
    RETURN QUERY
    SELECT 
      p.id,
      p.email,
      NULL::TEXT as first_name,
      NULL::TEXT as last_name,
      p.full_name,
      p.created_at,
      COALESCE(p.client_id, CONCAT('DCB', SUBSTRING(p.id::TEXT, 1, 6))) as client_id
    FROM profiles p
    WHERE 
      p.email ILIKE '%' || search_term || '%' OR
      p.full_name ILIKE '%' || search_term || '%'
    ORDER BY p.created_at DESC
    LIMIT page_limit;
  EXCEPTION
    WHEN OTHERS THEN
      RAISE NOTICE 'Profiles search failed: %', SQLERRM;
      RETURN;
  END;
END;
$function$
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        |
