                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          |
| CREATE OR REPLACE FUNCTION public.update_crypto_balance(p_user_id uuid, p_crypto_type character varying, p_amount numeric, p_operation character varying DEFAULT 'add'::character varying)
 RETURNS json
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
    v_balance_column TEXT;
    v_current_balance DECIMAL(18, 8);
    v_new_balance DECIMAL(18, 8);
    v_result JSON;
BEGIN
    -- Determine the balance column based on crypto type
    CASE p_crypto_type
        WHEN 'BTC' THEN v_balance_column := 'btc_balance';
        WHEN 'ETH' THEN v_balance_column := 'eth_balance';
        WHEN 'USDT' THEN v_balance_column := 'usdt_balance';
        ELSE RAISE EXCEPTION 'Unsupported cryptocurrency type: %', p_crypto_type;
    END CASE;
    
    -- Get current balance
    v_current_balance := get_crypto_balance(p_user_id, p_crypto_type);
    
    -- Calculate new balance based on operation
    CASE p_operation
        WHEN 'add' THEN
            v_new_balance := v_current_balance + p_amount;
        WHEN 'subtract' THEN
            v_new_balance := GREATEST(v_current_balance - p_amount, 0);
        WHEN 'set' THEN
            v_new_balance := p_amount;
        ELSE
            RAISE EXCEPTION 'Invalid operation: %. Must be add, subtract, or set', p_operation;
    END CASE;
    
    -- Ensure balance is not negative
    IF v_new_balance < 0 THEN
        RAISE EXCEPTION 'Balance cannot be negative. Current: %, Attempted change: %', v_current_balance, p_amount;
    END IF;
    
    -- Update balance using dynamic SQL
    EXECUTE format('
        INSERT INTO newcrypto_balances (user_id, %I, created_at, updated_at)
        VALUES ($1, $2, NOW(), NOW())
        ON CONFLICT (user_id)
        DO UPDATE SET %I = $2, updated_at = NOW()
    ', v_balance_column, v_balance_column) 
    USING p_user_id, v_new_balance;
    
    -- Return success result
    v_result := json_build_object(
        'success', true,
        'crypto_type', p_crypto_type,
        'operation', p_operation,
        'previous_balance', v_current_balance,
        'new_balance', v_new_balance,
        'amount_changed', p_amount
    );
    
    RETURN v_result;
    
EXCEPTION
    WHEN OTHERS THEN
        -- Return error result
        v_result := json_build_object(
            'success', false,
            'error', SQLERRM
        );
        RETURN v_result;
END;
$function$
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    |
